var express=require("express"),router=express.Router();const fs=require("fs");router.post("/:document_id",function(e,r,o){const t=e.params.document_id,n=e.body;fs.writeFile(`documents/${t}.json`,JSON.stringify(e.body),function(e){if(e)return console.log(e);r.send(`Saved ${JSON.stringify(n)} as ${t}.json`)})}),router.get("/:document_id",function(e,r,o){const t=require("json-beautify"),n=e.params.document_id;fs.readFile(`documents/${n}.json`,"utf8",(e,o)=>{if(e)return void console.error(e);const n=t(JSON.parse(o),null,2,1);r.send(n)})}),module.exports=router;